import { Component, Input } from '@angular/core';
import { Location } from '@angular/common';
import { ActivatedRoute, Router } from '@angular/router';
import { PlayersService } from '../players.service';
import { Data } from '../interface.data';
import { TranslateService } from '@ngx-translate/core';

@Component({
  selector: 'app-historial',
  templateUrl: './historial.component.html',
  styleUrls: ['./historial.component.scss']
})
export class HistorialComponent {

  id : any;
  onePlayer : any;
  eq1:boolean = false;
  eq2:boolean = false;
  eq3:boolean = false;
  eq4:boolean = false;
  eq5:boolean = false;


  constructor(public location: Location, public router: Router, public route: ActivatedRoute, public playerService : PlayersService) {
    this.route.params.subscribe(params => {this.id = params['id']});
    console.log(this.id);

    this.onePlayer=this.playerService.getPlayerOne(this.id);
    console.log(this.onePlayer)

    setTimeout(() => {
    this.eq1 = this.onePlayer.Club_anterior_1 ? true: false;
    this.eq2 = this.onePlayer.Club_anterior_2 ? true: false;
    this.eq3 = this.onePlayer.Club_anterior_3 ? true: false;
    this.eq4 = this.onePlayer.Club_anterior_4 ? true: false;
    this.eq5 = this.onePlayer.Club_anterior_5 ? true: false;
    }, 500);



  }


  goBack() {
    this.location.back();
  }


}
