import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { map } from 'rxjs/operators';
import { Data } from './interface.data';

@Injectable({
  providedIn: 'root'
})
export class PlayersService {

  private url = 'data.json';
  public data: Data = {
    players: [], id: 0, Nombre: '', Apellido: '', Edad: 0, Nacionalidad: '', Club_actual: '', Club_anterior_1: '', Club_anterior_2: '', Club_anterior_3: '', Club_anterior_4: '', Club_anterior_5: '', Aceleracion: 0, Velocidad: 0, Posicion: 0, Finalizacion: 0, Potencia_de_tiro: 0, Tiros_lejanos: 0, Voleas: 0, Penaltis: 0, Vision_de_juego: 0, Centros: 0, Precision_tirando_faltas: 0, Pases_cortos: 0, Pases_largos: 0, Efecto: 0, Agilidad: 0, Equilibrio: 0, Reaccion: 0, Control_del_Balon: 0, Regates: 0, Compostura: 0, Intercepciones: 0, Precision_de_cabezazos: 0, Capacidad_defensiva: 0, Entrada_normal: 0, Entrada_agresiva: 0, Salto: 0, Resistencia: 0, Fuerza: 0, Agresividad: 0, url: '',
    length: 0
  };

  id : any;

  constructor(private http: HttpClient) {
    this.getPlayers();
  }

  getPlayers(): void {
    if (this.data.length < 1) {
      this.http.get(this.url).pipe(
        map((response: any) => response.players)
      ).subscribe(data => this.data = data);
    }
  }

  getPlayerOne(id: any) {

    console.log(this.getPlayers())

    return this.data.players.find((player: Data) => player.id == id);

  }
}
