import { Component } from '@angular/core';
import { Location } from '@angular/common';
import { ActivatedRoute, Router } from '@angular/router';
import { PlayersService } from '../players.service';
import { Data } from '../interface.data';
import { TranslateService } from '@ngx-translate/core';

@Component({
  selector: 'app-players',
  templateUrl: './players.component.html',
  styleUrls: ['./players.component.scss']
})
export class PlayersComponent {
  id : any;
  onePlayer : any;

  constructor(
    public location: Location,
    public router: Router,
    public route: ActivatedRoute,
    public playerService : PlayersService,
    public translate: TranslateService) 
    {

    this.route.params.subscribe(params => {this.id = params['id']});
    console.log(this.id);

    this.onePlayer=this.playerService.getPlayerOne(this.id);
    console.log(this.onePlayer)

    this.setAppLang();
  }

  setAppLang():void{
    this.translate.setDefaultLang('es');
    this.translate.use(this.translate.getBrowserLang()!);
  }

  goBack() {
    this.location.back();
  }
  goNext(){
    this.router.navigate(['home/video/'+(this.id)])
  }

}

<div class="title">
    <h3 class="text-center">{{"Ficha"|translate}}<h2>{{ onePlayer.Nombre }} {{ onePlayer.Apellido }}</h2></h3>
</div>

<div class="grid-container point">
    <div class="grid-item photo" style="justify-content: center;">
        {{id.url}}
        <img src="../assets/images/{{onePlayer.url}}">
    </div>
    <div class="grid-item cardfifa">
        <div class="container">
            <div class="row">
              <div class="col-md-6">
                <div class="rounded p-3">
                  <h5>{{"Información del jugador"|translate}}</h5>
                  <br>
                  <ul>
                    <li><span><strong>{{"ID"}}:</strong> {{ onePlayer.id }}</span></li>
                    <li><strong>{{"Nombre"|translate}}:</strong> {{ onePlayer.Nombre }} {{ onePlayer.Apellido }}</li>
                    <li><strong>{{"Edad"|translate}}:</strong> {{ onePlayer.Edad }}</li>
                    <li><strong>{{"Nacionalidad"|translate}}:</strong> {{ onePlayer.Nacionalidad }}</li>
                    <li><strong>{{"Clubactual"|translate}}:</strong> {{ onePlayer.Clubactual }}</li>
                    <!-- <li><strong>{{"Clubanterior1"|translate}}:</strong> {{ onePlayer.Clubanterior1 }}</li>
                    <li><strong>{{"Clubanterior2"|translate}}:</strong> {{ onePlayer.Clubanterior2 }}</li>
                    <li><strong>{{"Clubanterior3"|translate}}:</strong> {{ onePlayer.Clubanterior3 }}</li>
                    <li><strong>{{"Clubanterior4"|translate}}:</strong> {{ onePlayer.Clubanterior4 }}</li>
                    <li><strong>{{"Clubanterior5"|translate}}:</strong> {{ onePlayer.Clubanterior5 }}</li> -->
