import { Injectable} from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Injectable({
  providedIn: 'root'
})
export class PlayersService {

  private url = 'data.json';
  public data : any[]=[];

  constructor(private http: HttpClient) {
    this.getPlayers()
  }

  getPlayers():void {
    if (this.data.length <1)
    {this.http.get(this.url).subscribe((data)=>this.data)}
  }

  getPlayerOne(id:any){
    return this.data.find((data)=> data.player.Nombre ==id)
  }


}
